console.log("Скрипт который работает вездe");const iconMenu=document.querySelector(".menu__icon"),iconMenuSpan=document.querySelector(".menu__icon > span"),menuBody=document.querySelector(".header__menu");iconMenu.addEventListener("click",function(e){iconMenu.classList.toggle("_active"),menuBody.classList.toggle("_active"),window.onclick=function(e){e.target!==menuBody&&e.target!==iconMenu&&e.target!==iconMenuSpan&&(menuBody.classList.remove("_active"),iconMenu.classList.remove("_active"))}});const headerButton=document.querySelector(".header__in__btn");window.screen.width<=443&&(headerButton.innerHTML="вход в кабинет"),window.screen.width<=373&&(headerButton.innerHTML="кабинет");const anchors=document.querySelectorAll('a[href*="#"]');for(const e of anchors)e.addEventListener("click",function(o){o.preventDefault();const n=e.getAttribute("href");document.querySelector(n).scrollIntoView({behavior:"smooth",block:"start"})});let lastScroll=0;const defaultOffset=200,header=document.querySelector(".header"),scrollPosition=()=>window.pageYOffset||document.documentElement.scrollTop,containHide=()=>header.classList.contains("_hide");window.addEventListener("scroll",()=>{scrollPosition()>lastScroll&&!containHide()&&scrollPosition()>200?header.classList.add("_hide"):scrollPosition()<lastScroll&&containHide()&&(header.classList.remove("_hide"),menuBody.classList.remove("_active"),iconMenu.classList.remove("_active")),lastScroll=scrollPosition()});